  //     </div>
  //   );
  // }
  // HASTA ACÁ

  return (
    <div className={Style.allCards}>
      <h1>Hola</h1>
      {
        ciudades.map((curCiudad) => {
          return (
            <Card
              key={curCiudad.id}
              max={curCiudad.main.temp_max}
              min={curCiudad.main.temp_min}
              name={curCiudad.name}
              img={curCiudad.weather[0].icon}
              onClose={() => alert(curCiudad.name)}
            />
          );
          // main --> temp_max
          // img --> weather 0 icon
        })
    }
    </div>
  )
}

